mapper.select:
select lfNO, lfTITLE, lfCONTENT, lfWriter, lfDate 
from(
	select rownum rnum, n1.* from(select * from LifeRULE order by lfNO desc
	) n1
)where rnum between #{start} and #{end} and boundary=1

select lfNO, lfTITLE, lfCONTENT, lfWriter, lfDate 
from(
	select rownum rnum, n1.* from(select * from LifeRULE order by lfNO desc
	) n1
)where rnum between #{start} and #{end} and boundary=2
